mapper.select:
select loan.id, loan.loan_no, loan.customer_id, customer.account as customerAccount, customer.name as customerName, loan.price, loan.loan_type, loan.apply_executor_id, ( select employee.name from employee where employee.id = loan.apply_executor_id ) as applyExecutor, loan.apply_date, loan.audit_type, loan.auditor_id, ( select employee.name from employee where employee.id = loan.auditor_id ) as auditorName, loan.audit_date, loan.audit_opinion, loan.grant_executor_id, ( select employee.name from employee where employee.id = loan.grant_executor_id ) as grantExecutor, loan.grant_date, loan.loan_status, loan.interest_rate, loan.repay_date, loan.term_repay_principal, loan.term_repay_interest, loan.term_repay_price, loan.repay_method, loan.repay_term, loan.loan_date, loan.late_charge_base, loan.loan_purpose, loan.recovered_interest, customer.phone as customerPhone from loan join customer on loan.customer_id = customer.id where 1=1
and loan.loan_no like concat('%',#{param.loanNo},'%')
and customer.account like concat('%',#{param.customerAccount},'%')
and customer.name like concat('%',#{param.customerName},'%')
and loan.audit_type = #{param.auditType}
and loan.loan_status = #{param.loanStatus}
and loan.repay_method = #{param.repayMethod}
and customer.phone like concat('%',#{param.customerPhone},'%')
and loan.apply_date >= #{param.applyStartDate}
and loan.apply_date <= #{param.applyEndDate}
order by case when loan.auditor_id is not null then loan.audit_date else loan.apply_date end desc
